import React, { useState, useEffect } from 'react';

const Matchups = () => {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [matchups, setMatchups] = useState([]);

  useEffect(() => {
    fetch('https://api.sleeper.app/v1/league/863187571134541824/matchups/6')
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setMatchups(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  let hashMap = {}; //stores the matchups of the current week
  let totalScores = {};
  let roster = {
    1: 'Nikul',
    2: 'Sanjay',
    3: 'Kush',
    4: 'Sam',
    5: 'Sudeep',
    6: 'Harsh',
    7: 'Somith',
    8: 'Snehansh',
    9: 'Evan',
    10: 'Mo',
    11: 'Ajit',
    12: 'Nihal',
  };

  //hashMap that stores the matchups of the current week
  for (let i = 0; i < matchups.length; i++) {
    let team = matchups[i];
    if (hashMap[team.matchup_id]) {
      hashMap[team.matchup_id].push(team.roster_id);
    } else {
      hashMap[team.matchup_id] = [team.roster_id];
    }
  }

  //harshMap that stores the total scores of the current team
  for (let i = 0; i < matchups.length; i++) {
    let team = matchups[i];
    const totalSum = (team) => {
      let counter = 0;
      for (let i = 0; i < team.starters_points.length; i++) {
        counter += team.starters_points[i];
      }
      return counter;
    };

    totalScore[team.roster_id] = totalSum(team);
  }

  console.log('this is hashMap', hashMap);

  return (
    <div>
      {Object.keys(hashMap).map((matchup) => (
        <h3>
          {/* {hashMap[matchup].map((team) => (
            <span>{roster[team]} vs.</span>
          ))} */}
          {roster[hashMap[matchup][0]]} vs. {roster[hashMap[matchup][1]]}
        </h3>
      ))}
    </div>
  );
};

export default Matchups;
